import React, { Component } from 'react'
import axios from 'axios'


class Trips extends Component {
    constructor (props) {
        super(props)

        this.state = {
            title: "",
            date:"",
            location: "",
            campers: "",
            items: ""
        }
    }

    changeHandler = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    submitHandler = e => {
        e.preventDefault()
        console.log(this.state)
        axios.post("mongodb+srv://user_atlas:KaP23G43H5JjcPm@cluster0.lhnjo.mongodb.net/Test?retryWrites=true&w=majority", this.state)
            .then(response => {
                console.log(response)
            })
            .catch(error => {
                console.log(error)
            })
    }

    render() {
        const { title, date, location, campers, items} = this.state
        return (
            <div>
                <form onSubmit={this.submitHandler}>
                    <div>
                        <input 
                        type="text" 
                        name="title" 
                        value={title} 
                        onChange={this.changeHandler}
                        />
                    </div>
                    <div>
                        <input 
                        type="text" 
                        name="date" 
                        value={date} 
                        onChange={this.changeHandler}
                        />
                    </div>
                    <div>
                        <input 
                        type="text" 
                        name="location" 
                        value={location}
                        onChange={this.changeHandler}
                        />
                    </div>
                    <div>
                        <input 
                        type="text" 
                        name="campers" 
                        value={campers}
                        onChange={this.changeHandler} 
                        />
                    </div>
                    <div>
                        <input 
                        type="text" 
                        name="items" 
                        value={items}
                        onChange={this.changeHandler} 
                        />
                    </div>
                    <button type="submit">Submit</button>
                </form>
            </div>
        )
    }
}

export default Trips;
